#define FP_MAX            65504.0
#define FP_SCALE          0.0009765625
#define FP_SCALE_INV      1024.0
#define GRAY_VECTOR       vec3(0.299, 0.587, 0.114)
#define CC_LIGHTMAP_DISABLED 0
#define CC_LIGHTMAP_ALL_IN_ONE 1
#define CC_LIGHTMAP_CC_LIGHTMAP_INDIRECT_OCCLUSION 2
in vec3 a_position;
in vec2 a_texCoord;
in vec4 a_color;
out vec4 color;
out vec2 uv0;
out vec2 uv1;
layout(std140) uniform Constants {
  vec4 otherParam;
};
vec4 vert () {
  vec4 pos = vec4(a_position, 1);
  #if USE_LOCAL
    pos = cc_matWorld * pos;
  #endif
  #if USE_PIXEL_ALIGNMENT
    pos = cc_matView * pos;
    pos.xyz = floor(pos.xyz);
    pos = cc_matProj * pos;
  #else
    pos = cc_matViewProj * pos;
  #endif
  color = a_color;
  uv0 = a_texCoord;
  #if SAMPLE_FROM_RT
    uv0 = cc_cameraPos.w > 1.0 ? vec2(uv0.x, 1.0 - uv0.y) : uv0;
  #endif
  uv1 = a_texCoord;
  uv1 = cc_cameraPos.w > 1.0 ? vec2(uv1.x, 1.0 - uv1.y) : uv1;
  float uv_extended = otherParam.x * 2.;
  uv1 *= 1. + uv_extended * 2.;
  uv1 -= vec2(uv_extended);
