glsl3.frag:
precision highp float;
vec4 CCSampleWithAlphaSeparated(sampler2D tex, vec2 uv) {
#if CC_USE_EMBEDDED_ALPHA
  return vec4(texture(tex, uv).rgb, texture(tex, uv + vec2(0.0, 0.5)).r);
#else
  return texture(tex, uv);
#endif
}
#if USE_ALPHA_TEST
  layout(std140) uniform ALPHA_TEST_DATA {
    float alphaThreshold;
  };
#endif
void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
    if (color.a < alphaThreshold) discard;
  #endif
}
void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
    if (alpha < alphaThreshold) discard;
  #endif
}
in vec4 color;
layout(std140) uniform FontSettings {
  vec4 gradientMultiply;
  vec4 innerShadow1;
  vec4 innerShadow2;
  vec4 innerShadowParam;
};
#if USE_TEXTURE
  in vec2 uv0;
  uniform sampler2D cc_spriteTexture;
  in vec2 uv1;
  uniform sampler2D gradientMap;
#endif
vec4 frag () {
  vec4 o = vec4(1.);
  #if USE_TEXTURE
    o *= CCSampleWithAlphaSeparated(cc_spriteTexture, uv0);
    vec4 g1 = texture(gradientMap, uv1);
    vec4 g2 = gradientMultiply;
    o.rgb = g1.rgb * g2.rgb;
    vec4 c1 = innerShadow1;
    vec4 c2 = innerShadow2;
    vec4 s1 = texture(cc_spriteTexture, uv0 + vec2(0., -innerShadowParam.x));
    vec4 s2 = texture(cc_spriteTexture, uv0 + vec2(0., innerShadowParam.y));
    if (s1.a < s2.a) {
      float t = o.a - s1.a;
      t *= innerShadowParam.z;
      o.rgb = mix(o.rgb, c1.rgb, t);
    }
    else {
      float t = o.a - s2.a;
      t *= innerShadowParam.w;
      o.rgb = mix(o.rgb, c2.rgb, t);
    }
  #endif
  o *= color;
  ALPHA_TEST(o);
  return o;
}
layout(location = 0) out vec4 cc_FragColor;
void main() { cc_FragColor = frag(); }

